package com.Arrayprogramme;

public class Swap2NosWithoutUsing3rdVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		a=a+b; //a= 10+20=30
		b=a-b;//b= 30-20=10
		a=a-b;//a= 30-10=20
		System.out.println("a= : "+a);
		System.out.println("b= : "+b);

	}

}
